"use strict";const logoName=document.querySelector(".header-top__logo-name"),nameLogoText=logoName.querySelectorAll("span"),logoImg=document.querySelector(".header-top__logo-img");logoImg.addEventListener("mouseenter",e=>{nameLogoText.forEach(o=>{e.target&&!o.classList.contains("logo__active")&&(o.classList.add("logo__active"),setTimeout(()=>{o.classList.remove("logo__active"),o.classList.add("logo__clean")},2500),setTimeout(()=>{o.classList.remove("logo__clean")},3500))})});const menu=document.querySelector(".menu__burger"),menuBurger=menu.querySelectorAll("span"),menuMax=document.querySelector(".header-top__menu");function showClassBurger(e){e.classList.toggle("active__burger")}menu.addEventListener("click",e=>{menuBurger.forEach(e=>{showClassBurger(e),menu.classList.toggle("active__burger"),menuMax.classList.toggle("active__burger")})});